mapper.sql:
from ecc_indicator where 1 = 1
and JjzbNy = #{parm.fjjzbNy}
order by Hyfl

mapper.select:
select count(*) 
from ecc_indicator where 1=1

		and JjzbNy  = #{jjzbNy}
		
		
		and Hyfl  = #{type}

 
       select case when Hyfl='1' then '生物/医药技术业' when Hyfl='2' then '电子与信息业'  when Hyfl='3' then '新材料技术/新材料业' when Hyfl='4' then '展览服务' when Hyfl='6' then '工业'  else '其他' end HyflName,ID ,Hybh ,Rzqy,Hyfl,Zczj,JgmzsrBys,JgmzsrLjs,LrzeBys,LrzeLjs,NsBys,NsLjs,LszeBys,LszeLjs,Ch,Zgs,Yfjf,Gxjscpsr,Gyzcz,Gyzjz,JjzbNy,Shzt
	from  ecc_indicator
		where 1 = 1		
			
		and JjzbNy  = #{jjzbNy}
		
		
		and Hyfl  = #{type}
		
		order by id desc

select sum(Zczj) sumZczj , sum(JgmzsrBys) sumJgmzsrBys, sum(JgmzsrLjs) sumJgmzsrLjs, sum(LrzeBys) sumLrzeBys, sum(LrzeLjs) sumLrzeLjs , sum(NsBys) sumNsBys , sum(NsLjs) sumNsLjs, sum(LszeBys) sumLszeBys, sum(LszeLjs) sumLszeLjs, sum(Ch) sumCh, sum(Zgs) sumZgs, sum(Yfjf) sumYfjf, sum(Gxjscpsr) sumGxjscpsr, sum(Gyzcz) sumGyzcz, sum(Gyzjz) sumGyzjz from ecc_indicator where 1 = 1
and JjzbNy = #{jjzbNy}
and Hyfl = #{type}
order by id desc
