mapper.sql:
from member_basic where 1 = 1
and qymc like CONCAT('%', #{parm.fhymc}, '%')
and hybh = #{parm.fhybh}
and str_to_date(Qyrzsj,'%Y-%m-%d') >= str_to_date(#{parm.frysjf},'%Y-%m-%d')
and str_to_date(Qyrzsj,'%Y-%m-%d') <= str_to_date(#{parm.frysje},'%Y-%m-%d')
and str_to_date(Htqxf,'%Y-%m-%d') >= str_to_date(#{parm.fhtqxf},'%Y-%m-%d')
and str_to_date(Htqxe,'%Y-%m-%d') <= str_to_date(#{parm.fhtqxe},'%Y-%m-%d')
and sfjjyb = #{parm.fsfjjyb}
and zt = #{parm.fzt}
order by ID

mapper.select:
select case when Zt='1' then '在园' else '出园' end ZtName,case when Sfjjyb='1' then '需要' else '不需要' end SfjjybName,ID,Hybh,Qymc,Qymcpy,Address,Zydy,Mj,Frdb,Zczb,Lxr,Zt,Qyrzsj,Htqxf,Htqxe,Qydjzclx,Lxrdh,Sfjjyb,Zzjgdm,Frlxdh,Qyclsj,Htstze,Dzys,Xnyjdxs,Qyssjsly,Gxjsqy,Lxryqy,Hylb,Synyxzscq,Fmzl,Wgsj,Syxx,Rjzzq from member_basic where 1 = 1 and Zt = '1'
and qymc like CONCAT('%', #{fhymc}, '%')
and str_to_date(Qyrzsj,'%Y-%m-%d') >= str_to_date(#{frysjf},'%Y-%m-%d')
and str_to_date(Qyrzsj,'%Y-%m-%d') <= str_to_date(#{frysje},'%Y-%m-%d')
and str_to_date(Htqxf,'%Y-%m-%d') >= str_to_date(#{fhtqxf},'%Y-%m-%d')
and str_to_date(Htqxe,'%Y-%m-%d') <= str_to_date(#{fhtqxe},'%Y-%m-%d')
and hybh = #{fhymc}
and sfjjyb = #{fsfjjyb}
and zt = #{fzt}
order by id desc

select count(*) from member_basic where 1 = 1 and Zt = '1'
and qymc like CONCAT('%', #{fhymc}, '%')
and str_to_date(Qyrzsj,'%Y-%m-%d') >= str_to_date(#{frysjf},'%Y-%m-%d')
and str_to_date(Qyrzsj,'%Y-%m-%d') <= str_to_date(#{frysje},'%Y-%m-%d')
and str_to_date(Htqxf,'%Y-%m-%d') >= str_to_date(#{fhtqxf},'%Y-%m-%d')
and str_to_date(Htqxe,'%Y-%m-%d') <= str_to_date(#{fhtqxe},'%Y-%m-%d')
and hybh = #{fhymc}
and sfjjyb = #{fsfjjyb}
and zt = #{fzt}

