mapper.sql:
		from member_intellectual_pro mip,member_basic mb where mip.MemberNo=mb.Hybh
		
		and mip.Year=#{parm.year}
		
		
		and mip.Month=#{parm.month}
		
		
		and mb.MemberNo=#{parm.memberNo}
		
		
		order by mip.ApplyTime desc

mapper.select:
select (@rowNO := @rowNo+1) AS RowNo, ttable.* from (select mip.MemberNo,mip.ProjectType,mip.ProjectName,mip.ApplyAmount,mip.SetUpAmount,mip.ApplyTime,
case when mip.IsSetUp='0' then '未知' when mip.IsSetUp='1' then '否' else '是' end IsSetUp ,
case when mip.ProjectLevel='0' then '国家级' when mip.ProjectLevel='1' then '省级' when mip.ProjectLevel='2' then '市级' else '区级' end ProjectLevel,
 mb.Qymc
from member_intellectual_pro mip,member_basic mb where mip.MemberNo=mb.Hybh

and mip.Year=#{year}


and mip.Month=#{month}

order by mip.Year desc,mip.Month desc
) ttable,(select @rowNO :=0) b

mapper.update:
update member_intellectual_pro set 
 										ProjectLevel=#{projectLevel}
 								,		ProjectType=#{projectType}
 								,		ProjectName=#{projectName}
 								,		ApplyAmount=#{applyAmount}
 								,		IsSetUp=#{isSetUp}
 								,		SetUpAmount=#{setUpAmount}
 								,		Year=#{year}
 								,		Month=#{month}
 							where Id = #{id}

		update member_intellectual_pro set 
  									Status=#{status}
		 							where Id = #{id}
