mapper.sql:
		from member_intellectual_pro mip,member_basic mb where mip.MemberNo=mb.Hybh
		
		
		and mip.ApplyTime between #{parm.startTime} and #{parm.endTime}
		
		
		order by mip.ApplyTime desc

mapper.select:
select (@rowNO := @rowNo+1) AS RowNo, ttable.* from (select mip.MemberNo,mip.ProjectType,mip.ProjectName,mip.ApplyAmount,mip.SetUpAmount,mip.ApplyTime, case when mip.IsSetUp='1' then '立项' else '不立项' end IsSetUp , case when mip.ProjectLevel='0' then '国家级' when mip.ProjectLevel='1' then '省级' when mip.ProjectLevel='2' then '市级' else '区级' end ProjectLevel, mb.Qymc from member_intellectual_pro mip,member_basic mb where mip.MemberNo=mb.Hybh
and mip.ApplyTime between #{startTime} and #{endTime}
order by mip.ApplyTime desc ) ttable,(select @rowNO :=0) b

mapper.update:
update member_intellectual_pro set 
 										ProjectLevel=#{projectLevel}
 								,		ProjectType=#{projectType}
 								,		ProjectName=#{projectName}
 								,		ApplyAmount=#{applyAmount}
 								,		IsSetUp=#{isSetUp}
 								,		SetUpAmount=#{setUpAmount}
 								,		ApplyTime=#{applyTime}
 							where Id = #{id}

		update member_intellectual_pro set 
  									Status=#{status}
		 							where Id = #{id}
