mapper.sql:
	from member_patent mp,member_basic mb   where mp.MemberNo=mb.Hybh
	
	and mp.Year=#{parm.year}
	
	
	and mp.Month=#{parm.month}
	
	
	and mp.MemberNo=#{parm.memberNo}
	
	order by mp.ApplyTime desc

mapper.select:
select (@rowNO := @rowNo+1) AS RowNo, ttable.* from (select mp.*, mb.Qymc 
from member_patent mp,member_basic mb   where mp.MemberNo=mb.Hybh

and mp.Year=#{year}


and mp.Month=#{month}

order by mp.Year desc,mp.Month desc

) ttable,(select @rowNO :=0) b

mapper.update:
update member_patent set 
 										Type=#{type}
 								,		Name=#{name}
 								,		PatentNo=#{patentNo}
 								,		Year=#{year}
 								,		Month=#{month}
 							where Id = #{id}

		update member_patent set 
  									Status=#{status}
		 							where Id = #{id}
